import { Alchemy, Network } from 'alchemy-sdk';
import { useEffect, useState } from 'react';
import { utils, BigNumber } from 'ethers';

import './App.css';

// Refer to the README doc for more information about using API
// keys in client-side code. You should never do this in production
// level code.
const settings = {
  apiKey: process.env.REACT_APP_ALCHEMY_API_KEY,
  network: Network.ETH_MAINNET,
};


// // In this week's lessons we used ethers.js. Here we are using the
// // Alchemy SDK is an umbrella library with several different packages.
// //
// // You can read more about the packages here:
// //   https://docs.alchemy.com/reference/alchemy-sdk-api-surface-overview#api-surface
const alchemy = new Alchemy(settings);

function App() {
  const [blockNumber, setBlockNumber] = useState();

  useEffect(() => {
    // async function getBlockNumber() {
    //   setBlockNumber(await alchemy.core.getBlockNumber());
    // }
    // getBlockNumber();
    // return <div className="App">Block Number: {blockNumber}</div>;
    BLOCKS_TO_SEARCH = 3;

    async function getTxnHistory() {
      const txnHistory = [];
      const blockNumber = await alchemy.core.getBlockNumber();
      for (let i = blockNumber - BLOCKS_TO_SEARCH; i <= blockNumber; i++) {
        console.log("block number:" + i);
        const block = await alchemy.core.getBlockWithTransactions(i);
        const blockTransactions = block.transactions;
        const dateObj = new Date(block.timestamp * 1000);
        const date = dateObj.toLocaleString();
        let txnRecord = {};
        for (let j = 0; j < blockTransactions.length; j++){
          if (blockTransactions[j].from == address || blockTransactions[j].to == address) {
              const gasFee = blockTransactions[j].gasPrice * blockTransactions[j].gasLimit;
              let txnURL = "https://etherscan.io/tx/" + blockTransactions[j].hash;
              txnRecord['Date'] = date;
              txnRecord['Block Number'] = i.toString();
              if (blockTransactions[j].from == address) {
                  txnRecord['Txn Type'] = 'Sent';
                  txnRecord['Counterparty'] = blockTransactions[j].to;
              };
              if (blockTransactions[j].to == address) {
                  txnRecord['Txn Type'] = 'Received';
                  txnRecord['Counterparty'] = blockTransactions[j].from;
              };
              txnRecord['Amount'] = ethers.utils.formatEther(ethers.BigNumber.from(parseInt(blockTransactions[j].value._hex, 16).toString()));
              txnRecord['Gas Fee'] = ethers.utils.formatEther(ethers.BigNumber.from(parseInt(gasFee.toString(16))));
              txnRecord['Etherscan Txn Record'] = txnURL;
              txnHistory.push(txnRecord);
              console.log(txnRecord);
          };
        };
      };
      return txnHistory;
    };

    // const address = document.getElementById("public-address").value;
    // const blocksToSearch = document.getElementById("blocks-to-search").value;
    // const response = await axios.get(`${server}/get-address-txns/${address}/${blocksToSearch}`);
    const txnHistory = getTxnHistory();
    let html = "<table class='table'>" +
               "<tr>" +
                "<th>Date</th>" +
                "<th>Block Number</th>" +
                "<th>Txn Type</th>" +
                "<th>Counterparty</th>" +
                "<th>Amount (ETH)</th>" +
                "<th>Gas Fee (ETH)</th>" +
                "<th>Etherscan Txn Record</th>" +
                "</tr>";
    for (let i = 0; i < txnHistory.length; i++) {
        html += "<tr>" + "<td>" + txnHistory[i]['Date'] + "</td>"
        + "<td>" + txnHistory[i]['Block Number'] + "</td>"
        + "<td>" + txnHistory[i]['Txn Type'] + "</td>"
        + "<td>" + txnHistory[i]['Counterparty'] + "</td>"
        + "<td>" + txnHistory[i]['Amount'] + "</td>"
        + "<td>" + txnHistory[i]['Gas Fee'] + "</td>"
        + "<td>" + "<a href='" + txnHistory[i]['Etherscan Txn Record'] + "'>" 
        + txnHistory[i]['Etherscan Txn Record'] + "</a>" + "</td>" + "</tr>";
    };
    html += "</table>";
    return html;

  });




}

export default App;
